select ss.bikes_available, ss.docks_available, (bd.date_string || ' ' || bt.time_string) as 'timestamp', bt.time_of_day, st.start_station_short_name as 'stationID', st.start_station_name as 'station_name', st.start_station_latitude as 'station_latitude', st.start_station_longitude as 'station_longitude', st.start_station_elevation as 'station_elevation', st.start_station_capacity from station_status ss LEFT JOIN bdate bd on ss.date_id=bd.date_id LEFT JOIN btime bt on ss.time_id=bt.time_id LEFT JOIN start_station st on ss.start_station_id=st.start_station_id WHERE ss.start_station_id=197;